      //   address: 'New York No. 1 Lake Park',
      //   tags: ['nice', 'developer'],
      // },
      // {
      //   key: '2',
      //   name: 'Jim Green',
      //   age: 42,
      //   address: 'London No. 1 Lake Park',
      //   tags: ['loser'],
      // },
      // {
      //   key: '3',
      //   name: 'Joe Black',
      //   age: 32,
      //   address: 'Sidney No. 1 Lake Park',
      //   tags: ['cool', 'teacher'],
      // },
    ];
    return (
      <div>
        <Table columns={columns} dataSource={data} locale={{emptyText: 'Fuck me'}}/>
      </div>
    )
  }
}

export default Demo;
